),

month as(
	select
		m.circle_id,
		c.n1_circle_name,
		m.origin,
		cast(m.month as timestamp) as month,
		m.cost
	from month_forecast m
	join {{ ref('circles') }} c
	on m.circle_id = c.circle_id
)

SELECT
	COALESCE(current_month.origin, prev_month.origin) as origin
	,COALESCE(current_month.circle_id, prev_month.circle_id) as circle_id
	,COALESCE(current_month.n1_circle_name, prev_month.n1_circle_name) as n1_circle_name
    ,COALESCE(current_month.month, prev_month.month + interval '1' month) as month
	,COALESCE(current_month.cost, 0) as cost
    ,COALESCE(prev_month.month, current_month.month - interval '1' month)  as previous_month
	,COALESCE(prev_month.cost, 0) as previous_cost
FROM
    month current_month
FULL OUTER JOIN
    month prev_month
ON
    current_month.origin = prev_month.origin
	AND current_month.circle_id = prev_month.circle_id
	AND current_month.n1_circle_name = prev_month.n1_circle_name
    AND current_month.month - interval '1' month = prev_month.month
ORDER BY
    3,1 DESC



